mapper.select:
SELECT venta.*, cliente.nombre AS nombre_cliente, cliente.ap_paterno AS ap_paterno_cliente, cliente.ap_materno AS ap_materno_cliente, cliente.email AS email_cliente, usuario.nombre AS nombre_usuario, usuario.ap_paterno AS ap_paterno_usuario, usuario.ap_materno AS ap_materno_usuario, usuario.email AS email_usuario, usuario.comision AS comision_usuario, caja.ds_descripcion AS descripcion_caja, caja.fecha_apertura AS fecha_apertura_caja, estacion.ds_descripcion AS descripcion_estacion, estado.ds_descripcion AS descripcion_estado FROM c_venta venta INNER JOIN c_cliente cliente ON (venta.cl_cliente = cliente.cl_cliente) INNER JOIN c_usuario usuario ON (venta.cl_usuario = usuario.cl_usuario) INNER JOIN c_caja caja ON (venta.cl_caja = caja.cl_caja) INNER JOIN c_estacion_trabajo estacion ON (venta.cl_estacion_trabajo = estacion.cl_estacion_trabajo) INNER JOIN c_estado_venta estado ON (venta.cl_estado_venta = estado.cl_estado_venta)
AND venta.cl_caja = #{cajaId}
AND venta.ds_descripcion LIKE CONCAT('%',#{descripcionFiltro},'%')
AND cliente.nombre LIKE CONCAT('%',#{nombreClienteFiltro},'%')
AND venta.cl_usuario = #{idUsuarioFiltro}
AND venta.cl_estado_venta = #{estadoVentaFiltro}
AND venta.fecha_registro >= CONCAT(#{fechaInicioFiltro},' 000000') AND venta.fecha_registro <= CONCAT(#{fechaFinFiltro},' 235959')
AND venta.fecha_registro >= CONCAT(#{fechaInicioFiltro},' 000000')
ORDER BY venta.cl_venta DESC
